values$spectra <- stri_encode(values$spectra , "", "UTF-8")



n <- length(values$spectra)

if (n > 200) {

      n = 200
}

# Spectra plot ---------------------------------------------------

f <- list(
      family = "Calibri",
      size = 18,
      color = toRGB('black')
)

ax <- list(
      title = "Wavelength",
      showline = TRUE,
      zeroline = FALSE,
      titlefont = f,
      gridwidth = 2,
      linewidth = 2
)
ay <- list(
      title = "Intensity",
      showline = TRUE,
      zeroline = FALSE,
      titlefont = f,
      gridwidth = 2,
      linewidth = 2
      
)


g <- plot_ly() %>%
      layout(xaxis = ax, yaxis = ay)

